fn match_command(input: &str) -> Result<&'static str, String> {
    let matches: Vec<&str> = COMMANDS
        .iter()
        .filter(|cmd| cmd.starts_with(input))
        .copied()
        .collect();

    match matches.len() {
        0 => Err(format!(
            "Unknown command '{}'. Available commands: {}",
            input,
            COMMANDS.join(", ")
        )),
        1 => Ok(matches[0]),
        _ => Err(format!(
            "Ambiguous command '{}'. Matches: {}",
            input,
            matches.join(", ")
        )),
    }
}

