fn run_vitals(addr: &str, loop_mode: bool) {
    if loop_mode {
        run_vitals_loop(addr);
    } else {
        match get_vitals(addr) {
            Ok(vitals) => print_vitals(&vitals),
            Err(e) => {
                eprintln!("Error fetching vitals: {}", e);
                std::process::exit(1);
            }
        }
    }
}

fn run_vitals_loop(addr: &str) {
    terminal::enable_raw_mode().expect("Failed to enable raw mode");
    let mut stdout = stdout();

    loop {
        // Clear screen and move cursor to top
        execute!(stdout, Clear(ClearType::All), MoveTo(0, 0)).unwrap();

        match get_vitals(addr) {
            Ok(vitals) => {
                print_vitals(&vitals);
                println!();
                println!("  Press ESC or Ctrl+C to exit");
            }
            Err(e) => {
                println!("Error fetching vitals: {}", e);
                println!();
                println!("  Press ESC or Ctrl+C to exit");
            }
        }
        stdout.flush().unwrap();

        // Check for key press with 1 second timeout
        if event::poll(Duration::from_secs(1)).unwrap() {
            if let Event::Key(key_event) = event::read().unwrap() {
                match key_event.code {
                    KeyCode::Esc => break,
                    KeyCode::Char('c') if key_event.modifiers.contains(KeyModifiers::CONTROL) => {
                        break
                    }
                    _ => {}
                }
            }
        }
    }

    terminal::disable_raw_mode().expect("Failed to disable raw mode");
    execute!(stdout, Clear(ClearType::All), MoveTo(0, 0)).unwrap();
}

fn run_version(addr: &str) {
    match get_version(addr) {
        Ok(version) => {
            println!("Tesla Wall Connector Version Info:");
            println!("  Firmware Version: {}", version.firmware_version);
            println!("  Git Branch:       {}", version.git_branch);
            println!("  Part Number:      {}", version.part_number);
            println!("  Serial Number:    {}", version.serial_number);
            println!("  Web Service:      {}", version.web_service);
        }
        Err(e) => {
            eprintln!("Error fetching version: {}", e);
            std::process::exit(1);
        }
    }
}

fn main() {
    let args = Args::parse();

    let command = match match_command(&args.command) {
        Ok(cmd) => cmd,
        Err(e) => {
            eprintln!("{}", e);
            std::process::exit(1);
        }
    };

    match command {
        "lifetime" => run_lifetime(&args.addr),
        "version" => run_version(&args.addr),
        "vitals" => run_vitals(&args.addr),
        "wifi_status" => run_wifi_status(&args.addr),
        _ => unreachable!(),
    }
}

